@import url("https://fonts.googleapis.com/css2?family=Lobster&family=Poppins:wght@300;400;500&display=swap");
// font-family: 'Lobster', cursive;
// font-family: 'Poppins', sans-serif;
$header-font-family: "Poppins", cursive;
$font-color: rgb(107, 113, 127);
// $font-color: black;

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html {
  font-size: 16px;
}
body {
  font-family: "Poppins", sans-serif;
  line-height: 1.7;
}
.heading2 {
  font-family: $header-font-family;
  font-size: 2.1875rem;
  letter-spacing: 1.5px;
  color: $font-color;
}
.white {
  color: white !important;
  padding: 0.625rem;
}
.text {
  font-size: 1.125rem;
  color: $font-color;
  text-align: justify;
}
.black-section {
  padding: 0.9375rem;
  background-color: rgb(0, 0, 0);
  .heading2 {
    margin: 0.625rem 0 0 0;
  }
  .white {
    margin-bottom: 1.5625rem;
  }
}
// Header Section Style :
.header {
  background-color: rgb(248, 247, 247);
  position: relative;
  padding: 3.125rem;
  height: 40vh;
  .heading1 {
    font-size: 5rem;
    font-weight: 300;
    // letter-spacing: 2px;
    top: 18vh;
    left: 8vw;
    transform: translate(50%, 50%);
    position: absolute;
    color: $font-color;
  }

  .logo-box {
    .logo {
      width: 11.25rem;
      top: 1.25rem;
      left: 1.875rem;
      position: absolute;
    }
  }
  .header--main {
    display: flex;
    justify-content: flex-end;

    .header--main--content {
      display: block;
      margin: 0.625rem;
      font-size: 1.875rem;
      a,
      a:link,
      a:active {
        color: rgb(22, 8, 75);
        text-decoration: none;
        font-weight: 500;
        padding: 0.375rem;
        &:hover {
          background-color: rgb(15, 3, 58);
          color: white;
        }
      }
    }
  }
  .inputbox {
    margin: 0.625rem 0.625rem 0.625rem 1.25rem;
    .inputtext {
      font-size: 1.0625rem;
      padding: 0.4375rem;
      border: 2px solid silver;
      color: grey;
    }
  }
}

// awesome--section style
.awesome--section {
  display: flex;
  justify-content: space-around;
  flex-shrink: 1;
  //   flex-wrap: wrap;
  .awesome--section--content {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 50%;
    font-size: 2rem;
    padding: 0.625rem;
    color: rgba(107, 113, 127, 0.6);
    font-weight: 300;
  }
  .awesome--section--logo {
    padding: 0.625rem;
    width: 50%;
    .glass {
      width: 100%;
    }
  }
}

// info class style ;
.info {
  background-color: $font-color;
  display: flex;
  justify-content: center;
  color: white;
  list-style: none;
  padding: 0.9375rem 0 1.5625rem 0;
  .link {
    padding: 0 0.625rem;
    font-weight: 500;
  }
  a,
  a:link,
  a:active {
    color: white;
    padding: 0.1875rem;
  }
  a:hover {
    text-decoration: none;
    background-color: rgb(137, 123, 147);
  }
}

//  Gird section style here :
.grid-section {
  padding: 3.125rem;
  margin-top: 1.25rem;
  display: grid;
  grid-template-rows: repeat(2, auto);
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 1.875rem;
  .box {
    // border: 2px solid blue;
    padding: 0.625rem;
    h2 {
      // text-align: center;
      margin-bottom: 1.25rem;
    }
  }
}
//  footer style here:
.footer {
  background-color: rgb(248, 247, 247);
  display: flex;
  align-items: center;
  justify-content: space-between;
  // flex-shrink: 1;
  flex-wrap: wrap;
  padding: 1.875rem;
  .footer--content {
    margin: 1.875rem 0;
    .text {
      max-width: 1000px;
    }
    .linklist {
      margin-top: 1.25remx;
      display: flex;
      list-style: none;
      a,
      a:link,
      a:active {
        border: 2px solid white;
        color: black;
        text-decoration: none;
        padding: 0.3125rem;
        margin: 0.3125rem;
        &:hover {
          background-color: rgb(228, 227, 240);
        }
      }
    }
  }
  .btn {
    margin-right: 3.125rem;
    a,
    a:link,
    a:active {
      padding: 0.3125rem 0.625rem;
      text-decoration: none;
      color: white;
      background-color: rgb(136, 111, 226);
      border: none;
      border-radius: 0.625rem;
      &:hover {
        background-color: rgb(112, 89, 199);
      }
    }
  }
}
