@import url("https://fonts.googleapis.com/css2?family=Lobster&family=Poppins:wght@300;400;500&display=swap");
// font-family: 'Lobster', cursive;
// font-family: 'Poppins', sans-serif;
$header-font-family: "Poppins", cursive;
$font-color: rgb(107, 113, 127);
// $font-color: black;

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html {
  font-size: 16px;
}
body {
  font-family: "Poppins", sans-serif;
  line-height: 1.7;
}
.heading2 {
  font-family: $header-font-family;
  font-size: 2.1875rem;
  letter-spacing: 1.5px;
  color: $font-color;
}
.white {
  color: white !important;
  padding: 0.625rem;
}
.text {
  font-size: 1.125rem;
  color: $font-color;
  text-align: justify;
}
.black-section {
  padding: 0.9375rem;
  background-color: rgb(0, 0, 0);
  .heading2 {
    margin: 0.625rem 0 0 0;
  }
  .white {
    margin-bottom: 1.5625rem;
  }
}
// Header Section Style :
.header {
  background-color: rgb(248, 247, 247);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  .navber {
    display: flex;
    align-items: flex-start;
    justify-content: space-between;
    width: 100%;
    .logobox {
      margin-top: 1rem;
      width: 20%;
      padding: 2px;

      .logo {
        width: 100%;
        height: 120px;
      }
    }
    .navber--content {
      width: 60%;
      display: flex;
      align-items: flex-start;
      justify-content: space-between;
      margin-top: 2rem;
      margin-right: 0.8rem;
      .navber--content--item {
        list-style: none;
        a,
        a:link,
        a:active {
          text-decoration: none;
          padding: 4px;
          color: rgb(14, 35, 83);
          font-size: 20px;
          font-weight: 500;
          &:hover {
            color: white;
            background-color: rgb(14, 24, 47);
          }
        }
      }
      .inputbox {
        .input {
          padding: 10px 1rem;
          border: 2px solid silver;
          font-size: 1rem;
          color: grey;
        }
      }
    }
  }
  .heading1 {
    color: $font-color;
    font-size: 4vw;
    letter-spacing: 2px;
    font-weight: 300;
    margin: 3rem 0 1rem 0;
  }
}
//  End of header style

// awesome--section style
.awesome--section {
  display: flex;
  justify-content: space-around;
  flex-shrink: 1;
  //   flex-wrap: wrap;
  .awesome--section--content {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 50%;
    font-size: 2rem;
    padding: 0.625rem;
    color: rgba(107, 113, 127, 0.6);
    font-weight: 300;
  }
  .awesome--section--logo {
    padding: 0.625rem;
    width: 50%;
    .glass {
      width: 100%;
    }
  }
}

// info class style ;
.info {
  background-color: $font-color;
  display: flex;
  justify-content: center;
  color: white;
  list-style: none;
  padding: 0.9375rem 0 1.5625rem 0;
  .link {
    padding: 0 0.625rem;
    font-weight: 500;
  }
  a,
  a:link,
  a:active {
    color: white;
    padding: 0.1875rem;
  }
  a:hover {
    text-decoration: none;
    background-color: rgb(137, 123, 147);
  }
}

//  Gird section style here :
.grid-section {
  padding: 3.125rem;
  margin-top: 1.25rem;
  display: grid;
  grid-template-rows: repeat(2, auto);
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 1.875rem;
  .box {
    // border: 2px solid blue;
    padding: 0.625rem;
    h2 {
      // text-align: center;
      margin-bottom: 1.25rem;
    }
  }
}
//  footer style here:
.footer {
  background-color: rgb(248, 247, 247);
  display: flex;
  align-items: center;
  justify-content: space-between;
  // flex-shrink: 1;
  flex-wrap: wrap;
  padding: 1.875rem;
  .footer--content {
    margin: 1.875rem 0;
    .text {
      max-width: 1000px;
    }
    .linklist {
      margin-top: 1.25remx;
      display: flex;
      list-style: none;
      a,
      a:link,
      a:active {
        border: 2px solid white;
        color: black;
        text-decoration: none;
        padding: 0.3125rem;
        margin: 0.3125rem;
        &:hover {
          background-color: rgb(228, 227, 240);
        }
      }
    }
  }
  .btn {
    margin-right: 3.125rem;
    a,
    a:link,
    a:active {
      padding: 0.3125rem 0.625rem;
      text-decoration: none;
      color: white;
      background-color: rgb(136, 111, 226);
      border: none;
      border-radius: 0.625rem;
      &:hover {
        background-color: rgb(112, 89, 199);
      }
    }
  }
}
.hidden {
  display: none !important;
}
// documentaion page style::

.documentation {
  padding: 10px;
  border: 2px solid blue;
  display: flex;
  align-items: center;
  .documentation--content {
    border: 2px solid blue;
    width: 50%;
    margin-right: 1rem;
    li {
      margin: 1rem 5px;
      list-style: none;
    }
    a,
    a:link,
    a:active {
      text-decoration: none;
      padding: 5px 10px;
      border: 1px solid silver;
      font-size: 1.2rem;
    }
  }
  .documentation--information {
    border: 2px solid yellow;
    width: 70%;
    .mutetext {
      display: flex;
      justify-content: center;
      align-items: center;
      text-align: justify;
      font-size: 2rem;
      padding: 0.625rem;
      color: rgba(107, 113, 127, 0.6);
      font-weight: 300;
      margin-bottom: 1rem;
    }
  }
}
